    private void Sumar1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Sumar1ActionPerformed
        // TODO add your handling code here:
        int suma1, suma2, totsuma;
        
        String numero1= num1.getText();
        String numero2= num2.getText();
                
        suma1=Integer.parseInt(numero1);
        suma2=Integer.parseInt(numero2);
        totsuma=(suma1+suma2);
        
        System.out.println("El valor es "+totsuma);
        String total=("La suma es = "+ totsuma);
        resultado.setText(total);
        
        
    }//GEN-LAST:event_Sumar1ActionPerformed

