    private void DividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DividirActionPerformed
        
        double suma1, suma2, totsuma;
        
        String numero1= num1.getText();
        String numero2= num2.getText();
                
        suma1=Integer.parseInt(numero1);
        suma2=Integer.parseInt(numero2);
        totsuma=(suma1/suma2);
        
        System.out.println("El valor es "+totsuma);
        String total=("La suma es = "+ totsuma);
        resultado.setText(total);
        
        
    }//GEN-LAST:event_DividirActionPerformed

    private void ExponenteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ExponenteActionPerformed

                
        double totsuma = Math.pow(Double.parseDouble(num1.getText()), Double.parseDouble(num2.getText()));
        
        String total=("La suma es = "+ totsuma);
        resultado.setText(total);
                
    }//GEN-LAST:event_ExponenteActionPerformed

      
    
